// webview 
var $T = {}
//htmlcss
$T.addCss = function(css){
  var sheet = document.createElement('style')
  sheet.innerHTML = css;
  $('body').append(sheet);
}
//url
$T.getISUrl = function(id , width ,height){
  if (typeof id != "string")
    id = id.url.split("/")[id.url.split("/").length-1] 
  size = ""
  if (width)
		if (!height)
			size = "_"+width
		else 
			size = "_"+width+"x"+height+""
	else if (height)
			size = "_x"+height
	return "http://is.hudongka.com/"+id.split('.')[0]+size+"."+id.split('.')[1]
}
// .clickble data-event data-pagename data-params openactivity
$T.setViewEvents = function (){
    function evthandle(event){
        var obj = $(event.target);
        if( obj.parents(".clickble").length > 0 ){
            obj = obj.parents(".clickble");
        }
        var evtname = obj.attr("data-event");
        if (evtname && "openActivity" == evtname){
            postEvent("openActivity", "" , obj.attr("data-pagename") ,obj.attr("data-params"));
        }
    }
    $(".clickble").tap(evthandle);
}
//  .http-img  .local-img iscroll
$T.setViewStyle = function (){
    //    
    $(".local-img").each(function(index ,item){
        $(item).attr({"src":baseurl+$(item).attr("data-url")});
    });
    //     
    $(".http-img").each(function(index ,item){
        $(item).attr({"src":$T.getISUrl($(item).attr("data-url") , $(item).attr("data-width"),$(item).attr("data-height")) })
    });
    //input type = textarea  textarea
    $("input[type=textarea]").each(function(index,item){
        var parent = $(item).parent();
        textarea = $("<textarea><\/textarea>")
        if ($(item).attr("placeholder"))
            textarea.attr( "placeholder" , $(item).attr("placeholder") );
        if ($(item).attr("id"))
            textarea.attr( "id" , $(item).attr("id") );
        if ($(item).attr("name"))
            textarea.attr( "name" , $(item).attr("name") );
        parent.append(textarea);
        $(item).remove();
    })
    // scroll
    if($("#wrapper").length > 0){
        setTimeout(function(){
          scroll = new iScroll('wrapper', {
            snap: false,
            momentum: true, //
            hScroll: false,
            hScrollbar: false,
            onScrollEnd: function (e) {},
            onBeforeScrollStart: function (e) {
              var target = e.target;
              while (target.nodeType != 1) target = target.parentNode;
              if (target.tagName != 'SELECT' && target.tagName != 'INPUT' && target.tagName != 'TEXTAREA' && target.tagName != 'OPTION')
                e.preventDefault();
            },
            tap: true
          });
          // inputiscrollinput
          $(window).resize(function(){
            scroll.refresh();
            $("input:focus, textarea:focus").each(function(index, item){
              if (item === document.activeElement){
                scroll.scrollTo(0, -($(item).offset().top - $(item).css("height").substr(0,$(item).css("height").length - 2)), 200) 
              }
            });
          });
        },500);
    }
}

